var keySizeFinder = function(lowerBound, upperBound, text){
  var shortestDistanceAndSize = [-1,-1];
  for(var i = lowerBound; i <= upperBound; i++){
    var first = text.slice(0, i * 8).join('');
    var second = text.slice(i * 8, i * 16).join('');
    var third = text.slice(i * 16, i * 24).join('');
    var fourth = text.slice(i * 24, i * 32).join('');
    var distance1 = hammingDistance(first, second) / i;
    var distance2 = hammingDistance(third, fourth) / i;
    var distance = ((distance1 + distance2) / 2);
    if(shortestDistanceAndSize[0] === -1){
      shortestDistanceAndSize = [distance, i];
    }
    if(shortestDistanceAndSize[0] > distance){
      shortestDistanceAndSize = [distance, i];
    }
  }
  return shortestDistanceAndSize;
};

console.log(keySizeFinder(1, 40, binaryFirstPart))
