var chunkText = function(binaryText, keySize){
  var masterArray = [];
  var copy = binaryText.slice();
  var counter = 0;
  while(copy.length >= 8){
    var singleByte = copy.splice(0, 8).join('');
    if(counter === keySize){
      counter = 0;
    }
    if(!masterArray[counter]){
      masterArray[counter] = [singleByte];
    }else{
      masterArray[counter].push(singleByte)
    }
    counter++;
  }
  return masterArray;
};

